// dstat is ready to receive command
else if (acknowledge == command.length && received != command.length){
    msg.payload = command; 
    return msg 
} 

else if (p_request > 0){
    p_request = flow.get("parameters_request");
    p_chain = flow.get("parameter_chain");
    p_received = flow.get("parameters_received");
    p_error = flow.get("parameters_error");
    parameters = global.get("parameters");
    // when parameters received are equeal to request we scape
    // and restart counter
    if (p_received == p_request){
        flow.set("parameter_counter", 0);
        return 0
    } 
    // if not, we need to send one parameter at a time
    else {
        counter = flow.get("parameter_counter");
        p_request = flow.get("parameters_request");
        p_error = flow.get("parameters_error");

        // If counter has run through all the parameters, then ignore
        if (counter >= p_request){
            return 0
            // return 0;
        }
        // if there are errors, re-send last parameter
        else if (p_error == 1 && flow.get("sent_parameter")==0){
            counter = flow.get("parameter_counter");
            msg.payload = parameters[counter-1];
            flow.set("sent_parameter",1);
            return msg;
        } 
        else if (flow.get("sent_parameter")==0){
            counter = flow.get("parameter_counter");
            msg.payload = parameters[counter];
            flow.set("sent_parameter",1);
            return msg;
        }
    }
}

// command has been already received
else if (received == command.length || done == 1){
    return 0
}

